        <?php
            // Fetch recent users (use the existing function if already defined)
            function fetch_recent_users_with_groups() {
                global $wpdb;
            
                // Fetch the 20 most recent users
                $recent_users = $wpdb->get_results("
                    SELECT ID, display_name 
                    FROM {$wpdb->users} 
                    ORDER BY user_registered DESC 
                    LIMIT 20
                ");
            
                $users_with_groups = array();
            
                // Loop through each user to get their joined groups
                foreach ($recent_users as $user) {
                    $user_id = $user->ID;
            
                    // Fetch group names from user meta
                    $joined_groups = get_user_meta($user_id, 'joined_group_names');
            
                    // Add user data with groups to the array
                    $users_with_groups[] = array(
                        'display_name' => $user->display_name,
                        'joined_groups' => $joined_groups
                    );
                }
            
                return $users_with_groups;
            }
            
            // Fetch recent users with their groups
            $users_with_groups = fetch_recent_users_with_groups();
            ?>

        <!-- Carousel Container -->
        <div class="user-information-carousel">
            <div class="user-carousel">
                <?php foreach ($users_with_groups as $user_data) : ?>
