                    <strong><?php echo esc_html($user_data['display_name']); ?></strong>
                    <?php if (!empty($user_data['joined_groups'])) : ?>
                    <?php echo esc_html(implode(', ', $user_data['joined_groups'])); ?>

                    <?php else : ?>
                    <em>No joined groups</em>
                    <?php endif; ?>
