        <?php
 function fetch_recent_users_with_profile() {
    global $wpdb;

    // Query to get user IDs and display names
    $recent_users = $wpdb->get_results("
        SELECT ID, display_name 
        FROM {$wpdb->users}
        ORDER BY user_registered DESC
        LIMIT 20
    ");

    $users_with_profile_pictures = array();

    // Loop through each user to get their profile picture
    foreach ($recent_users as $user) {
        $user_id = $user->ID;

        // Fetch profile picture URL from user meta
        $profile_picture_url = get_user_meta($user_id, 'profile_picture_url', true);

        // Add user data to the array
        $users_with_profile_pictures[] = array(
            'user_id' => $user_id,
            'display_name' => $user->display_name,
            'profile_picture_url' => $profile_picture_url
        );
    }

    return $users_with_profile_pictures;
}

// Fetch recent users with profile pictures
$users_with_profile_pictures = fetch_recent_users_with_profile();
