                // Fetch recent users' story titles
                function fetch_recent_users_with_stories() {
                    global $wpdb;
                
                    // Query to get recent stories along with their authors
                    $recent_stories = $wpdb->get_results("
                        SELECT p.post_title, u.display_name, u.ID as user_id
                        FROM {$wpdb->posts} p
                        LEFT JOIN {$wpdb->users} u ON p.post_author = u.ID
                        WHERE p.post_type = 'post' AND p.post_status = 'publish'
                        ORDER BY p.post_date DESC
                        LIMIT 10
                    ");
                
                    return $recent_stories;
                }
                
                // Fetch recent users with their story titles
                $recent_users_with_stories = fetch_recent_users_with_stories();
